    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        Integer ID =new Integer(MemNo.getText());
        String Name= MemName.getText();

        try {
            Library.read("member.txt");
            Library.getlibrary().getMembers().addMember(new Members(Name, ID));

            Library.write("member.txt");
            clear();
            tableload();
            JOptionPane.showMessageDialog(rootPane, "Member Successfully Added");

        } catch (Exception e) {
            System.out.println("button error " +e);
        }

    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String sea=search.getText();
        if(sname.isSelected()){
            Members mem1b=Library.getlibrary().getMembers().getMemberFromName(sea);

            DefaultTableModel model=(DefaultTableModel) jTable1.getModel();
            model.setRowCount(0);

            model.addRow(new Object[]{mem1b.getmemnum(),mem1b.getmembername(),mem1b.getBooksOnLoan()});

        }
        else if(snumber.isSelected()){
            Integer i=new Integer(sea);
            Members mem1b=Library.getlibrary().getMembers().getMemberFromNumber(i);

            DefaultTableModel model=(DefaultTableModel) jTable1.getModel();
            model.setRowCount(0);

            model.addRow(new Object[]{mem1b.getmemnum(),mem1b.getmembername(),mem1b.getBooksOnLoan()});
        }
        else{
            JOptionPane.showMessageDialog(rootPane, "Please Select Search Method");
            tableload();
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
         int x=jTable1.getSelectedRow();
        MemNo.setText(jTable1.getValueAt(x, 0).toString());
        MemName.setText(jTable1.getValueAt(x, 1).toString());
    }//GEN-LAST:event_jTable1MouseClicked

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        int Mno=new Integer(MemNo.getText());
        String mNAme=MemName.getText();
        Library.read("member.txt");
        Members md=Library.getlibrary().getMembers().getMemberFromNumber(Mno);
        Library.getlibrary().getMembers().removeMember(md);
        Library.write("member.txt");
        MemNo.setText("");
        MemName.setText("");
        tableload();
    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        int Mno=new Integer(MemNo.getText());
        String mNAme=MemName.getText();
        if(MemNo.getText().equals("")||mNAme.equals("")){
            JOptionPane.showMessageDialog(rootPane, "All Field Required!!");
            
            
        }
        else{
       
        Library.read("member.txt");
        Members md=Library.getlibrary().getMembers().getMemberFromNumber(Mno);
        Library.getlibrary().getMembers().removeMember(md);
         Library.getlibrary().getMembers().addMember(new Members(mNAme, Mno));
        Library.write("member.txt");
        MemNo.setText("");
        MemName.setText("");
        tableload();
        }
    }//GEN-LAST:event_jButton4ActionPerformed

