    private void MemNoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_MemNoKeyTyped
         char c=evt.getKeyChar();
        String s=MemNo.getText();
         
         if(!(Character.isDigit(c))||(c==com.sun.glass.events.KeyEvent.VK_BACKSPACE) || (c== com.sun.glass.events.KeyEvent.VK_DELETE))
         {
             evt.consume();
         }
    }//GEN-LAST:event_MemNoKeyTyped

    private void MemNameKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_MemNameKeyTyped
         char c=evt.getKeyChar();
        String s=MemNo.getText();
         
         if((Character.isDigit(c))||(c==com.sun.glass.events.KeyEvent.VK_BACKSPACE) || (c== com.sun.glass.events.KeyEvent.VK_DELETE))
         {
             evt.consume();
         }
    }//GEN-LAST:event_MemNameKeyTyped

 public void tableload(){
        Library.read("member.txt");
        Members [] memb=Library.getlibrary().getMembers().getallmembers();
        DefaultTableModel model=(DefaultTableModel) jTable1.getModel();
        model.setRowCount(0);
        for(Members member:memb){
            model.addRow(new Object[]{member.getmemnum(),member.getmembername(),member.getBooksOnLoan().getSize()});
        }
        
    }
 
 public void clear(){
        MemName.setText("");
        MemNo.setText("");
    }
